CREATE TABLE [dbo].[VendorBalance]
(
	[ID] INT NOT NULL PRIMARY KEY IDENTITY,
    [VendorID] INT NULL,
    [VendorPaymentID] INT NULL,
    [OpeningBalance] DECIMAL(18, 2) NULL,
    [TotalBillAmount] DECIMAL(18, 2) NULL,
    [TotalGST] DECIMAL(18, 2) NULL,
    [TotalGST_TDS] DECIMAL(18, 2) NULL,
    [TotalNetPayable] DECIMAL(18, 2) NULL,
    [AmountPaid] DECIMAL(18, 2) NULL,
    [BalanceAmount] DECIMAL(18, 2) NULL,
    [Pending_BillAmount] DECIMAL(18, 2) NULL,
    [Pending_GST] DECIMAL(18, 2) NULL,
    [Pending_TDS] DECIMAL(18, 2) NULL,
    [Pending_GST_TDS] DECIMAL(18, 2) NULL,
    [Pending_NetPayable] DECIMAL(18, 2) NULL,
    [Pending_Paid] DECIMAL(18, 2) NULL,
    [CreatedBy] NVARCHAR(100) NULL,
    [CreatedOn] DATETIME2 NOT NULL DEFAULT GETUTCDATE(),
    [ModifiedBy] NVARCHAR(100) NULL,
    [ModifiedOn] DATETIME2 NOT NULL DEFAULT GETUTCDATE(),
    CONSTRAINT [FK_VendorBalance_ToVendor] FOREIGN KEY ([VendorID]) REFERENCES [Vendor]([ID]),
     CONSTRAINT [FK_VendorBalance_ToVendorPayment] FOREIGN KEY ([VendorPaymentID]) REFERENCES [VendorPayment]([ID]),
)
